import { useState, useEffect } from 'react';
import styled from 'styled-components';

const Chat = () => {
  const [messages, setMessages] = useState<string[]>([]);
  const [currentMessage, setCurrentMessage] = useState<string>('');
  const [socket, setSocket] = useState<WebSocket | null>(null);
  const [userName, setuserName] = useState<string>();
  useEffect(() => {
    const newSocket = new WebSocket('ws://192.168.0.23:4002');

    const generateName = (ip: string) => {
        if(ip ==='123.142.80.4')
        {
          return setuserName('선택');
        } else if(ip ==='192.168.0.18') {
          return setuserName('병석');
        } else if(ip === '192.168.0.31') {
          return setuserName('철민');
        } else {
          return setuserName(ip)
        }
    }
    // console.log(data.ip)
    // fetch('https://api64.ipify.org?format=json')
    // .then((response) => response.json())
    // .then((data) => {
    //   const userIP = data.ip;
    //   console.log('ip:',userIP);
    //   const usersName: any = generateName(userIP);
    //   console.log('name: ',usersName);
    //   setuserName(usersName);
    //   });
    newSocket.onopen = () => {
      console.log('WebSocket connection opened');
    };

    newSocket.onmessage = (event) => {
      const message = event.data;
      setMessages((prevMessages) => [...prevMessages, message]);
    };

    newSocket.onclose = () => {
      console.log('WebSocket connection closed');
    };

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  }, []);

  const sendMessage = () => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.send(currentMessage);
      setCurrentMessage('');
    }
  };
  const handleKeyPress = (e: any) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div>
      <H2>Chat Room</H2>
      <ChatContainer>
        <ul>
          {messages.map((message, index) => (
            <Li key={index}>{userName} : {message}</Li>
          ))}
        </ul>
      </ChatContainer>
      <ChatSendDiv>
        <ChatSendbar
          type="text"
          value={currentMessage}
          onChange={(e) => setCurrentMessage(e.target.value)}
          onKeyUp={handleKeyPress}
          placeholder="메시지 입력"
        />
        <ChatButton onClick={sendMessage}>Send</ChatButton>
      </ChatSendDiv>
    </div>
  );
};
const H2 = styled.h2`
  display:flex;
  margin: auto;
  justify-content:center;
  padding:20px;
`;
const ChatContainer = styled.div`
  display: grid;
  width: 750px;
  height:600px;
  border-radius:5px;
  margin:auto;
  justify-content:center;
  box-shadow: 1px 6px 6px 1px rgba(0, 0, 0, 0.1);
  overflow-y: scroll;
`;
const ChatButton = styled.button`
  background-color: rgba(55, 12, 23, 0.2);
  margin-left:5px;
  border:none;
  border-radius: 5px;
  width:75px;
  height:25px;
`;
const ChatSendbar = styled.input`
  border: none;
  background-color: rgba(55, 12, 23, 0.1);
  border-radius: 5px;
  width:275px;
  height:30px;
`;
const ChatSendDiv = styled.div`
  display:flex;
  margin:auto;
  margin-top:15px;
  justify-content:center;
  
`;

const Li = styled.li`
  list-style: none;
`;

export default Chat;
