import { AnimatedBackground } from "./style/global-styles";
import styled from 'styled-components';
import ShowImages from "./pages/ShowImages";
import { DelayedNavi, useLogout, ChecksToken } from './modules/Hooks';
// import mall from './images/mall.jpg';

function App() {
  const { isLoggedIn } = ChecksToken();
  const triggerNavi = DelayedNavi();
  const { handleLogout } = useLogout();

  const handleButtonClick = () => {
    triggerNavi('/PersonalPage');
  };

  const handlelogin = () => {
    triggerNavi('/login')
  }

  return (
    <>

      <AnimatedBackground/>
      {isLoggedIn ? (
        <Container>
          <button onClick={handleLogout}>
            <span>로그아웃</span>
          </button>
          <button onClick={handleButtonClick}>
            <span>개인페이지</span>
          </button>
        </Container>
      ) : (
        <Container>
          <button onClick={handlelogin}>
            <span>로그인</span>
          </button>
        </Container>
      )}
        <ShowImages />
    </>
  );
}

export default App;

const Container = styled.div`
  position: relative;
  width: 40%;
  height:40%;
  display:grid;
  margin:auto;
  margin-top:20%;
  margin-right:10%;
  border-radius: 10px;
  background-color: rgba(0, 0, 0, 0.4);
`;
