import { useState, useEffect } from "react";
import { GlobalStyle } from "./style/global-styles";
import { Link } from "react-router-dom";
import styled from 'styled-components';
import ShowImages from "./pages/ShowImages";
import {useDelayedNavigation} from './modules/Hooks';

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const ToPersonalPage = useDelayedNavigation();
  useEffect(() => {
    const storedToken = sessionStorage.getItem("__bluecapsule__");

    if (storedToken) {
      setLoggedIn(true);
    }
  }, []);






  const handleLogout = () => {
    setLoggedIn(false);
    sessionStorage.removeItem("__bluecapsule__");
  };

  return (
    <>
      <GlobalStyle /> {/* backgroundColor */}
      {isLoggedIn ? (
        <Container>
          <Button onClick={handleLogout}>
            <span>로그아웃</span>
          </Button>
            <Button onClick={ToPersonalPage}>
              <span>개인페이지</span>
            </Button>
        </Container>
      ) : (
        <Container>
          <Link to="/login">
            <Button>
              <span>로그인</span>
            </Button>
          </Link>
        </Container>
      )}
      <Container>
        <ShowImages />
      </Container>
    </>
  );
}

export default App;

const Container = styled.div`
  display:grid;
  justify-content: center;
  align-items: center;
  margin-top:25vh;


`;

const Button = styled.button`
  margin: auto;
  margin-top: 20px;
  background: rgb(0, 172, 238);
  background: linear-gradient(0deg, rgba(0, 172, 0, 1) 0%, rgba(2, 126, 0, 1) 100%);
  width: 200px;
  height: 40px;
  line-height: 42px;
  padding: 0;
  border: none;
  border-radius: 5px;
  position: relative;
  color: white;
  outline: none;
  box-shadow: inset 2px 2px 2px 0px rgba(255, 255, 255, 0.5),
    7px 7px 20px 0px rgba(0, 0, 0, 0.1),
    4px 4px 5px 0px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;

  &:before,
  &:after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    background: rgba(2, 126, 0, 1);
    transition: all 0.3s ease;
  }

  &:before {
    height: 0%;
    width: 2px;
  }

  &:after {
    width: 0%;
    height: 2px;
  }

  &:hover {
    background: transparent;
    box-shadow: none;

    &:before {
      height: 100%;
    }

    &:after {
      width: 100%;
    }

    span {
      color: rgba(2, 126, 0, 1);

      &:before {
        height: 100%;
      }

      &:after {
        width: 100%;
      }
    }
  }

  span {
    position: relative;
    display: block;
    width: 100%;
    height: 100%;

    &:before,
    &:after {
      content: "";
      position: absolute;
      left: 0;
      bottom: 0;
      background: rgba(2, 126, 0, 1);
      transition: all 0.3s ease;
    }

    &:before {
      width: 2px;
      height: 0%;
    }

    &:after {
      width: 0%;
      height: 2px;
    }
  }
`;
