import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Upload: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [userName, setUserName] = useState<string>(''); // Initialize with an empty string
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch the JWT token from local storage
    const storedToken = localStorage.getItem('__bluecapsule__');

    if (storedToken) {
      try {
        // Decode the token to get user information
        const decodedToken: any = parseJwt(storedToken);

        if (decodedToken && decodedToken.name) {
          setUserName(decodedToken.name);
        }
      } catch (error) {
        console.error('Error decoding token:', error);
      }
    }

    // Set loading to false to indicate that the initial fetch is complete
    setLoading(false);
  }, []); // Empty dependency array ensures this effect runs only once on component mount

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setFile(e.target.files[0]);
    }
  };

  const handleUpload = async () => {
    try {
      if (!file || !userName) {
        console.error('Please select a file and make sure the username is available');
        return;
      }

      const formData = new FormData();
      formData.append('image', file);
      formData.append('userName', userName);

      const response = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/upload`, {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Upload successful:', data);
        // Handle the response as needed
      } else {
        console.error('Upload failed:', response.statusText);
        // Handle error response
      }
    } catch (error: any) {
      console.error('Error during upload:', error.message);
      // Handle general error
    }
  };

  if (loading) {
    return <div>Loading...</div>; // You can render a loading indicator while fetching the token
  }

  return (
    <Container>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <input type="text" placeholder="Enter your name" value={userName} readOnly />
      <Button onClick={handleUpload}><span>Upload Image</span></Button>
    </Container>
  );
};

export default Upload;

function parseJwt(token: string): any {
  if (!token) {
    console.error('Token is undefined or null');
    return null;
  }

  const parts = token.split('.');

  if (parts.length !== 3) {
    console.error('Token does not have the expected structure');
    return null;
  }

  const base64Url = parts[1];

  if (!base64Url) {
    console.error('Base64Url is undefined or null');
    return null;
  }

  const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');

  try {
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split('')
        .map((c) => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
        .join('')
    );

    return JSON.parse(jsonPayload);
  } catch (error) {
    console.error('Error parsing JWT:', error);
    return null;
  }
}

const Container = styled.div `
    display: flex;
    margin:auto;
    margin-top: 50px;
`;

const Button = styled.button `
  margin: auto;
  margin-top: 20px;
  background: rgb(0, 172, 238);
  background: linear-gradient(0deg, rgba(0, 172, 0, 1) 0%, rgba(2, 126, 0, 1) 100%);
  width: 200px;
  height: 40px;
  line-height: 42px;
  padding: 0;
  border: none;
  border-radius: 5px;
  position: relative;
  color: white;
  outline: none;
  box-shadow: inset 2px 2px 2px 0px rgba(255, 255, 255, 0.5),
    7px 7px 20px 0px rgba(0, 0, 0, 0.1),
    4px 4px 5px 0px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;

  &:before,
  &:after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    background: rgba(2, 126, 0, 1);
    transition: all 0.3s ease;
  }

  &:before {
    height: 0%;
    width: 2px;
  }

  &:after {
    width: 0%;
    height: 2px;
  }

  &:hover {
    background: transparent;
    box-shadow: none;

    &:before {
      height: 100%;
    }

    &:after {
      width: 100%;
    }

    span {
      color: rgba(2, 126, 0, 1);

      &:before {
        height: 100%;
      }

      &:after {
        width: 100%;
      }
    }
  }

  span {
    position: relative;
    display: block;
    width: 100%;
    height: 100%;

    &:before,
    &:after {
      content: "";
      position: absolute;
      left: 0;
      bottom: 0;
      background: rgba(2, 126, 0, 1);
      transition: all 0.3s ease;
    }

    &:before {
      width: 2px;
      height: 0%;
    }

    &:after {
      width: 0%;
      height: 2px;
    }
  }
`;
