import React, { useState, useEffect } from 'react';
import styled from 'styled-components';


const ShowImages: React.FC = () => {

  const formatTime = (dateTimeString: string): string => {
    const options: Intl.DateTimeFormatOptions = {
      year: 'numeric',
      month: 'numeric',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: false,
    };

    const dateTime = new Date(dateTimeString);
    return dateTime.toLocaleString(undefined, options);
  };

  const [images, setImage] = useState<any[]>([]);

  useEffect(() => {

    const fetchImages = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/ShowImage`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        if (!response.ok) {
          throw new Error('Failed to fetch images');
        }

        const data = await response.json();
        setImage(data);
      } catch (error: any) {
        console.error('Error fetching images:', error);
      }
    };

    fetchImages();
  }, []);

  return (
    <>
      <H2>전체 게시물</H2>

      <ImageContainer>
        {images.map((image: any, index) => {
          console.log('Current image.img:', image.img); 
          return (
            <ImgContainer key={index}>
              <Image src={`${image.img}`} alt='Error loading image' />
              <p>
                Registered User <br />
                <strong>{image.name}</strong>
              </p>
              <p>
                Registration date <br />
                <strong>{formatTime(image.day)}</strong>
              </p>
            </ImgContainer>
          );
        })}
      </ImageContainer>
    </>
  );
};

export default ShowImages;

const H2 = styled.h2`
  text-align:center;
`
const ImgContainer = styled.div`
  text-align: center;
  width:200px;
  height:150px;
`;
const ImageContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Image = styled.img`
  width: 150px;
  height: 150px;
  border-radius:5px;
  object-fit: cover;
  margin: 5px;
`;
