import React, { useState, useEffect } from 'react';
import styled from 'styled-components';


const ShowImages: React.FC = () => {
    const [imageInfo, setImageInfo] = useState<any[]>([]);
  
    useEffect(() => {
      const fetchImages = async () => {
        try {
          const response = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/ShowAllImages`);
          if (!response.ok) {
            throw new Error('Failed to fetch images');
          }
  
          const data = await response.json();
          setImageInfo(data.data);
        } catch (error) {
          console.error('Error fetching images:', error);
        }
      };
  
      fetchImages();
    }, []);
  
    return (
      <>
        <h2>All Registered Images</h2>
        <div>
          {imageInfo.map((info, index) => (
            <div key={index}>
              <p>Name: {info.name}</p>
              <p>Image URL: {info.img}</p>
              <p>Upload Date: {info.day}</p>
              <img src={info.img} alt={`Image ${index}`} />
            </div>
          ))}
        </div>
      </>
    );
  };
  
  export default ShowImages;

const ImageContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Image = styled.img`
  width: 150px;
  height: 150px;
  object-fit: cover;
  margin: 5px;
`;
