import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import cryptoJs from 'crypto-js';

const secretKey = `${process.env.REACT_APP_DB_SECRETKEY_C}`;
const iv = `${process.env.REACT_APP_DB_IV_C}`;

// 암호화
export const encrypt = (text: string) => {
  const cipher = cryptoJs.AES.encrypt(text, cryptoJs.enc.Utf8.parse(secretKey), {
      iv: cryptoJs.enc.Utf8.parse(iv),
      padding: cryptoJs.pad.Pkcs7,
      mode: cryptoJs.mode.CBC,
  });

  return cipher.toString();
}

// 복호화
export const decrypt = (encryptedText: string) => {
  const decipher = cryptoJs.AES.decrypt(encryptedText, cryptoJs.enc.Utf8.parse(secretKey), {
      iv: cryptoJs.enc.Utf8.parse(iv),
      padding: cryptoJs.pad.Pkcs7,
      mode: cryptoJs.mode.CBC,
  });
  return decipher.toString(cryptoJs.enc.Utf8);
}

const Login: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [userName, setUserName] = useState<string | null>(null);
  const [token, setToken] = useState<string | null>(null);

  useEffect(() => {
    const storedToken = localStorage.getItem('__bluecapsule__');

    if (storedToken) {
      setToken(storedToken);

      const decryptedToken: any = decrypt(storedToken);
      const decodedToken: any = parseJwt(decryptedToken);
      setUserName(decodedToken.name);

      setLoggedIn(true);
    }
  }, []);

  const handleEmailChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPassword(e.target.value);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserName(null);
    setToken(null);
    localStorage.removeItem('__bluecapsule__');
  };

  const handleLogin = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    try {
      const response = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        const encryptedToken = data.encryptedToken; // Assuming the server returns the encrypted token
        console.log('Encrypted Token:', encryptedToken);

        // Decrypt the token before storing it
        const decryptedToken: any = decrypt(encryptedToken);

        setToken(decryptedToken);
        localStorage.setItem('__bluecapsule__', decryptedToken);

        const decodedToken: any = parseJwt(decryptedToken);
        setUserName(decodedToken.name);

        setLoggedIn(true);

      } else {
        const errorMessage = await response.text();
        console.error('Login failed:', errorMessage);
        alert("아이디 혹은 비밀번호가 잘못 입력됐따.")
      }
    } catch (error: any) {
      console.error('Error during login:', error.message);
    }
  };

  return (
    <LoginContainer>
      <Title>{isLoggedIn ? `${userName} 님 ㅎㅇ ` : 'Login'}</Title>
      {isLoggedIn ? (
        <LogoutForm>
          <Button onClick={handleLogout}>Logout</Button>
        </LogoutForm>
      ) : (
        <LoginForm onSubmit={handleLogin}>
          <Label>
            Email:
            <Input type="email" value={email} onChange={handleEmailChange} />
          </Label>
          <Label>
            Password:
            <Input type="password" value={password} onChange={handlePasswordChange} />
          </Label>
          <Button type="submit">Login</Button>
        </LoginForm>
      )}
    </LoginContainer>
  );
};

export default Login;



function parseJwt(token: string): any {
  const base64Url = token.split('.')[1];
  const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
  const jsonPayload = decodeURIComponent(atob(base64).split('').map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2)).join(''));
  return JSON.parse(jsonPayload);
}


const LoginContainer = styled.div` 
max-width: 400px;
margin: auto;
margin-top:50px;

  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h2`
  text-align: center;
  color: #333;
`;

const LoginForm = styled.form`
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  margin-top: 20px;
`;

const LogoutForm = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 20px;
`;
const Label = styled.label`

  margin-bottom: 10px;
  font-size: 16px;
  color: #555;
`;

const Input = styled.input`
  padding: 8px;
  font-size: 14px;
`;

const Button = styled.button`
  background-color: #4caf50;
  color: white;
  padding: 10px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
`;
