import styled from 'styled-components';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';


const PersonalPage: React.FC = () => {
  const [images, setImages] = useState<any[]>([]);
  const [userName, setUserName] = useState<string>('');

  useEffect(() => {
    const fetchImages = async () => {
      try{
        const response = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/ShowImage`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch images');
        }
        const data = await response.json();
        setImages(data.filter((images: any) => images.name === userName));
      } catch (error: any) {
          console.error('Error fetching images: ', error);
      }
    };
    fetchImages();
  }, [userName]);

  return (
    <>

      <Container>
        <h2>개인 페이지</h2>
        <Link to="/">
          <Button><span>메인 페이지</span></Button>
        </Link>
        <Link to="/Upload">
          <Button><span>이미지 업로드</span></Button>
        </Link>
      </Container>
      <br />
      <h2>등록한 이미지</h2>
      {images.map((image: any, index) => (
        <Container key={index}>
          <Image src={image.img} alt='이미지 로드 오류' />


        </Container>
      ))}
      <Container>

      </Container>
    </>
  );
}

export default PersonalPage;

const Image = styled.img`
    width: 150px;
    height: 150px;
    border-radius:5px;
    object-fit: cover;
    margin: 5px;
`;

const Container = styled.div`
    display: grid;
    justify-content: center;
    text-align:center;
`;

const Button = styled.button`
  margin: auto;
  margin-top: 20px;
  background: rgb(0, 172, 238);
  background: linear-gradient(0deg, rgba(0, 172, 0, 1) 0%, rgba(2, 126, 0, 1) 100%);
  width: 200px;
  height: 40px;
  line-height: 42px;
  padding: 0;
  border: none;
  border-radius: 5px;
  position: relative;
  color: white;
  outline: none;
  box-shadow: inset 2px 2px 2px 0px rgba(255, 255, 255, 0.5),
    7px 7px 20px 0px rgba(0, 0, 0, 0.1),
    4px 4px 5px 0px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;

  &:before,
  &:after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    background: rgba(2, 126, 0, 1);
    transition: all 0.3s ease;
  }

  &:before {
    height: 0%;
    width: 2px;
  }

  &:after {
    width: 0%;
    height: 2px;
  }

  &:hover {
    background: transparent;
    box-shadow: none;

    &:before {
      height: 100%;
    }

    &:after {
      width: 100%;
    }

    span {
      color: rgba(2, 126, 0, 1);

      &:before {
        height: 100%;
      }

      &:after {
        width: 100%;
      }
    }
  }

  span {
    position: relative;
    display: block;
    width: 100%;
    height: 100%;

    &:before,
    &:after {
      content: "";
      position: absolute;
      left: 0;
      bottom: 0;
      background: rgba(2, 126, 0, 1);
      transition: all 0.3s ease;
    }

    &:before {
      width: 2px;
      height: 0%;
    }

    &:after {
      width: 0%;
      height: 2px;
    }
  }
`;
