import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const ShowImages: React.FC = () => {

  const formatTime = (dateTimeString: string): string => { //날짜 형태 지정
    const options: Intl.DateTimeFormatOptions = {
      year: 'numeric',
      month: 'numeric',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: false,
    };

    const dateTime = new Date(dateTimeString);
    return dateTime.toLocaleString(undefined, options);
  };

  const [images, setImage] = useState<any[]>([]);
  const [visibleImages, setVisibleImages] = useState<number>(4);

  const fetchImages = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/ShowImage`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      if (!response.ok) {
        throw new Error('Failed to fetch images');
      }

      const data = await response.json();
      setImage(data);
    } catch (error: any) {
      console.error('Error fetching images:', error);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const handleLoadMore = () => {
    setVisibleImages((prevVisibleImages) => prevVisibleImages + 4);
  };

  return (
    <>
      <H2>전체 게시물</H2>
      <ImageContainer>
      {images.slice(0, visibleImages).map((image: any, index) => (
          <ImgContainer key={index}>
            <Image src={image.img} alt='이미지 로드 오류' />
            <p>
              <Span>등록 유저</Span><br/>
              <strong>{image.name}</strong>
            </p>
            <p>
              <Span>등록 일자</Span> <br />
              <strong>{formatTime(image.day)}</strong>
            </p>
          </ImgContainer>
          ))}
          {visibleImages < images.length && (
           <LoadMoreButton onClick={handleLoadMore}>더 보기</LoadMoreButton>
           )}
      </ImageContainer>
    </>
  );
};

export default ShowImages;

const Span = styled.span`
  font-size:15px;
  color: rgba(125, 125, 125, 1);
`;

const H2 = styled.h2`
  text-align:center;
`
const ImgContainer = styled.div`
  text-align: center;
  width:200px;
  height:300px;
`;
const ImageContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Image = styled.img`
  width: 120px;
  height: 120px;
  border-radius:5px;
  object-fit: cover;
  margin: 5px;
  cursor: pointer;
`;
const LoadMoreButton = styled.button`
  margin: 10px;
  padding: 8px 16px;
  background-color: #007bff;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;
