import React, { useState } from 'react';
import styled from 'styled-components';

const Registration: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isEmailDuplicate, setIsEmailDuplicate] = useState(false);
  const [isNameDuplicate, setIsNameDuplicate] = useState(false);

  const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setName(e.target.value);
    setIsNameDuplicate(false);
  };

  // 이름 중복 확인
  const handleCheckNameDuplicate = async (e: React.MouseEvent<HTMLButtonElement>) => {
    e.preventDefault();
    try {

      const response = await fetch(
        `${process.env.REACT_APP_API_SERVER_APP}/checkName`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ name }),
        }
      );

      if (response.ok) {
        const data = await response.json();
        if (data.isDuplicate) {
          setIsNameDuplicate(true);
          alert('이미 이름이 존재합니다.');
        } else {
          alert('사용 가능한 이름입니다.');
        }
      } else {
        console.error('Failed to check for duplicate name.');
      }
    } catch (error: any) {
      console.error('Error during duplicate check for name:', error.message);
    }
  };

  // 이메일 중복 확인
  const handleCheckEmailDuplicate = async (e: React.MouseEvent<HTMLButtonElement>) => {
    e.preventDefault();
    try {

      const response = await fetch(
        `${process.env.REACT_APP_API_SERVER_APP}/checkEmail`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email }),
        }
      );
      if (response.ok) {
        const data = await response.json();
        if (data.isDuplicate) {
          setIsEmailDuplicate(true);
          alert('이미 이메일이 존재합니다.');
        } else {
          alert('사용 가능한 이메일입니다.');
        }
      } else {
        console.error('Failed to check for duplicate email.');
      }
    } catch (error: any) {
      console.error('Error during duplicate check for email:', error.message);
    }
  };

  const handleEmailChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEmail(e.target.value);
    setIsEmailDuplicate(false);
  };

  const handlePasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPassword(e.target.value);
  };



  const handleResetForm = () => {
    setName('');
    setEmail('');
    setPassword('');
    setIsNameDuplicate(false);
    setIsEmailDuplicate(false);
  };


  //회원 가입 하기
  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    try {

      if (!name.trim() || !email.trim() || !password.trim()) {
        alert('공백은 입력할 수 없음.');
        return;
      }

      const duplicateEmailResponse = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/checkEmail`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      if (duplicateEmailResponse.ok) {
        const duplicateEmailData = await duplicateEmailResponse.json();
        if (duplicateEmailData.isDuplicate) {
          setIsEmailDuplicate(true);
          alert('이메일 있다자낭');
          return;
        }
      } else {
        console.error('Failed to check for duplicate email.');
        return;
      }

      const duplicateNameResponse = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/checkName`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name }),
      });

      if (duplicateNameResponse.ok) {
        const duplicateNameData = await duplicateNameResponse.json();
        if (duplicateNameData.isDuplicate) {
          setIsNameDuplicate(true);
          alert('이름 있다자낭');
          return;
        }
      } else {
        console.error('Failed to check for duplicate name.');
        return;
      }

      const registrationResponse = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, password }),
      });

      if (registrationResponse.ok) {
        const data = await registrationResponse.json();
        alert('회원가입이 완료되었습니다.');
        console.log('Registration successful:', data);
        handleResetForm(); // Reset the form after successful registration
      } else {
        const errorData = await registrationResponse.json();
        console.error('Registration failed:', errorData.error);
      }
    } catch (error: any) {
      alert('다시 시도해주세요.');
      console.error('Error during registration:', error.message);
    }
  };

  return (
    <RegistrationContainer>
      <Title>가입 ㅋㅋ</Title>
      <Form onSubmit={handleSubmit}>
        <Label>
          Name :
          <Input
            type="text"
            value={name}
            onChange={handleNameChange}
            style={{ borderColor: isNameDuplicate ? 'red' : '' }}
          />
          　
          {!name.trim() ? (
            <DisabledCheckDuplicatesButton disabled>
              중복 확인
            </DisabledCheckDuplicatesButton>
          ) : (
            <CheckDuplicatesButton
              onClick={handleCheckNameDuplicate}
              disabled={!name.trim()}
            >
              중복 확인
            </CheckDuplicatesButton>
          )}
          {/* {isNameDuplicate && <ErrorMessage>The name already exists</ErrorMessage>} */}
        </Label>
        <Label>
          Email : 
          <Input
            type="email"
            value={email}
            onChange={handleEmailChange}
            style={{ borderColor: isEmailDuplicate ? 'red' : '' }}
          />
          　
          {!email.trim() ? (
            <DisabledCheckDuplicatesButton disabled>
              중복 확인
            </DisabledCheckDuplicatesButton>
          ) : (
            <CheckDuplicatesButton
              onClick={handleCheckEmailDuplicate}
              disabled={!email}>
              중복 확인
            </CheckDuplicatesButton>
          )}

          {/* {isEmailDuplicate && <ErrorMessage>이미 이메일이 존재합니다.</ErrorMessage>} */}
        </Label>
        <Label>
          Password:
          <Input type="password" value={password} onChange={handlePasswordChange} />
        </Label>
        <Button type="submit">회원 가입</Button>
        <ResetButton type="button" onClick={handleResetForm}>
          모두 비우기
        </ResetButton>
      </Form>
    </RegistrationContainer>
  );
};

export default Registration;

const RegistrationContainer = styled.div`

  max-width: 400px;
  margin: auto;
  margin-top:50px;
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h2`
  text-align: center;
  color: #333;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  margin-top: 20px;
`;

const Label = styled.label`
  display: flex;
  margin-bottom: 10px;
  font-size: 16px;
  color: #555;
  justify-content: space-evenly;
  align-items: center;
`;


const Input = styled.input`
  padding: 8px;
  font-size: 14px;
`;

const Button = styled.button`
  margin-top: 20px;
  background: rgb(0, 172, 238);
  background: linear-gradient(0deg, rgba(0, 172, 0, 1) 0%, rgba(2, 126, 0, 1) 100%);
  width: 200px;
  height: 40px;
  line-height: 42px;
  padding: 0;
  border: none;
  border-radius: 5px;
  position: relative;
  color: white;
  outline: none;
  box-shadow: inset 2px 2px 2px 0px rgba(255, 255, 255, 0.5),
    7px 7px 20px 0px rgba(0, 0, 0, 0.1),
    4px 4px 5px 0px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;

  &:before,
  &:after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    background: rgba(2, 126, 0, 1);
    transition: all 0.3s ease;
  }

  &:before {
    height: 0%;
    width: 2px;
  }

  &:after {
    width: 0%;
    height: 2px;
  }

  &:hover {
    background: transparent;
    box-shadow: none;

    &:before {
      height: 100%;
    }

    &:after {
      width: 100%;
    }

    span {
      color: rgba(2, 126, 0, 1);

      &:before {
        height: 100%;
      }

      &:after {
        width: 100%;
      }
    }
  }

  span {
    position: relative;
    display: block;
    width: 100%;
    height: 100%;

    &:before,
    &:after {
      content: "";
      position: absolute;
      left: 0;
      bottom: 0;
      background: rgba(2, 126, 0, 1);
      transition: all 0.3s ease;
    }

    &:before {
      width: 2px;
      height: 0%;
    }

    &:after {
      width: 0%;
      height: 2px;
    }
  }
`;

const ResetButton = styled(Button)`
  background-color: #ccc;
  margin-top: 10px;
`;

const ErrorMessage = styled.span`
  color: red;
`;

const CheckDuplicatesButton = styled.button`
  background-color: #4caf50;
  color: black;
  padding: 5px 10px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  margin-top: 5px;
`;
const DisabledCheckDuplicatesButton = styled(CheckDuplicatesButton)`
  background-color: rgba(125, 125, 125, 0.5);
  color: white;
  cursor: not-allowed;
`;

export {
  RegistrationContainer,
  Title,
  Form,
  Label,
  Input,
  Button,
  ResetButton,
  ErrorMessage,
  CheckDuplicatesButton
};
