import { useState, useEffect } from 'react';
import styled from 'styled-components';

const Chat = () => {
  const [messages, setMessages] = useState<string[]>([]);
  const [currentMessage, setCurrentMessage] = useState<string>('');
  const [socket, setSocket] = useState<WebSocket | null>(null);

  useEffect(() => {
    const newSocket = new WebSocket('ws://192.168.0.23:4002');

    newSocket.onopen = () => {
      console.log('WebSocket connection opened');
    };

    newSocket.onmessage = async (event) => {
      try {
        const data = event.data;

        if (data instanceof Blob) {
          // Handle Blob data, convert it to a data URL
          const imageUrl = await blobToDataURL(data);
          setMessages((prevMessages) => [...prevMessages, imageUrl]);
        } else {
          // Regular text message
          setMessages((prevMessages) => [...prevMessages, data]);
        }
      } catch (error) {
        console.error('Error in onmessage:', error);
      }
    };

    newSocket.onclose = () => {
      console.log('WebSocket connection closed');
    };

    setSocket(newSocket);

    return () => {
      try {
        newSocket.close();
      } catch (error) {
        console.error('Error in cleanup:', error);
      }
    };
  }, []);

  const sendMessage = () => {
    try {
      if (socket && socket.readyState === WebSocket.OPEN) {
        socket.send(currentMessage);
        setCurrentMessage('');
      }
    } catch (error) {
      console.error('Error in sendMessage:', error);
    }
  };

  // Function to convert Blob to data URL
  const blobToDataURL = (blob: Blob) => {
    return new Promise<string>((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => {
        resolve(reader.result as string);
      };
      reader.onerror = reject;
      reader.readAsDataURL(blob);
    });
  };

  return (
    <div>
      <h2>Chat Room</h2>
      <div>
        <ul>
          {messages.map((message, index) => (
            <Li key={index}>
              {message.startsWith('data:image') ? (
                <img src={message} alt="Image" />
              ) : (
                message
              )}
            </Li>
          ))}
        </ul>
      </div>
      <div>
        <input
          type="text"
          value={currentMessage}
          onChange={(e) => setCurrentMessage(e.target.value)}
          placeholder="Type your message here"
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

const Li = styled.li`
  list-style: none;
`;

export default Chat;
