import { useLocation } from 'react-router-dom';
import { ShowPost } from '../modules/Hooks';
import styled from 'styled-components';
import { formatTime } from '../modules/Modules';

const Viewpost: React.FC = () => {
    const location = useLocation();
    const fileName = location.state && location.state.fileName;
    const { title, images, contents, userName, day } = ShowPost(fileName);

    return (
      <>
        <Customed_table>
          <tbody>
            <tr>
              <td>사진 영역</td>
              <td>
  {images && images.map((image, index) => (
    <img key={index} src={image} alt={`Image ${index}`} />
  ))}
</td>
            </tr>
            <tr>
              <td>테스트용 제목</td>
              <td>{title} </td>
            </tr>
            <tr>
              <td>게시물 내용</td>
              <td>{contents}</td>
            </tr>
            <tr>
              <td>유저명</td>
              <td>{userName}</td>
            </tr>
            <tr>
              <td>날짜</td>
              <td>{day &&formatTime(day)}</td>
            </tr>
          </tbody>
        </Customed_table>
      </>
    );
  }
  
  export default Viewpost;

const DIV = styled.div`
    position: relative;
    width: 400px;
    height:700px;
    background-color:white;
    box-shadow: 1px 6px 6px 1px rgba(0, 0, 0, 0.2);
    border-radius: 7.5px;
    margin-left:50px;
    margin-top: 40px;
`; 

const Customed_table = styled.table`

`
