import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const ShowImages: React.FC = () => {
  const formatTime = (dateTimeString: string): string => {
    const options: Intl.DateTimeFormatOptions = {
      year: 'numeric',
      month: 'numeric',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: false,
    };

    const dateTime = new Date(dateTimeString);
    return dateTime.toLocaleString(undefined, options);
  };

  const [images, setImage] = useState<any[]>([]);
  const [selectedImage, setSelectedImage] = useState<string | null>(null);

  const isValidImage = (url: string): boolean => {
    const supportedExtensions = ['.png', '.jpg', '.jpeg'];
    const extension = url.substring(url.lastIndexOf('.')).toLowerCase();
    return supportedExtensions.includes(extension);
  };

  const fetchImages = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/ShowImage`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      if (!response.ok) {
        throw new Error('Failed to fetch images');
      }

      const data = await response.json();
      setImage(data);
    } catch (error: any) {
      console.error('Error fetching images:', error);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const openLightbox = (url: string) => {
    setSelectedImage(url);
  };

  const closeLightbox = () => {
    setSelectedImage(null);
  };

  return (
    <>
      <H2>전체 게시물</H2>

      <ImageContainer>
        {images.map((image: any, index) => (
          isValidImage(image.img) && (
            <ImgContainer key={index} onClick={() => openLightbox(image.img)}>
              <Image src={image.img} alt='이미지 로드 오류' />
              <p>
                <Span>등록 유저</Span>
                <br />
                <strong>{image.name}</strong>
              </p>
              <p>
                <Span>등록 일자</Span>
                <br />
                <strong>{formatTime(image.day)}</strong>
              </p>
            </ImgContainer>
          )
        ))}
      </ImageContainer>

      {selectedImage && (
        <LightboxContainer>
          <LightboxImage src={selectedImage} alt='Selected Image' />
          <CloseButton onClick={closeLightbox}>Close</CloseButton>
        </LightboxContainer>
      )}
    </>
  );
};

export default ShowImages;

const Span = styled.span`
  font-size: 15px;
  color: rgba(125, 125, 125, 0.8);
`;

const H2 = styled.h2`
  text-align: center;
`;

const ImgContainer = styled.div`
  text-align: center;
  width: 200px;
  height: 150px;
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;

const ImageContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Image = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 5px;
  margin: 5px;
`;

const LightboxContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const LightboxImage = styled.img`
  max-width: 90%;
  max-height: 90%;
  border-radius: 5px;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background-color: transparent;
  color: white;
  border: none;
  cursor: pointer;
  font-size: 16px;
`;
