import React, { useState, useEffect } from "react";
import { GlobalStyle } from "./style/global-styles";
import { Link } from "react-router-dom";
import styled from 'styled-components';

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const storedToken = localStorage.getItem("__bluecapsule__");
    if (storedToken) {
      setLoggedIn(true);
    }
  }, []);

  const handleLogout = () => {
    setLoggedIn(false);
    localStorage.removeItem("__bluecapsule__");
  };

  return (
    <>
      <GlobalStyle /> {/* backgroundColor */}
      {isLoggedIn ? (
        <Container>
          <button type="button" onClick={handleLogout}>
            로그아웃
          </button>
          <Link to="/PersonalPage">
            <button type="button">개인페이지</button>
          </Link>
        </Container>
      ) : (
        <Container>
          <Link to="/login">
            <button type="button">로그인</button>
          </Link>
        </Container>
      )}

    </>
  );
}

export default App;

const Container = styled.div`
    width:100%;
    display: flex;
    margin: auto;
    justify-content: center;
    align-items: center;
`;
