import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link, useNavigate} from 'react-router-dom';
import parseJwt from '../modules/parseJwt';

const Upload: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [userName, setUserName] = useState<string>('');
  const Token: any = localStorage.getItem('__bluecapsule__');
  const decodedToken: any = parseJwt(Token);
  const navigate = useNavigate();

  useEffect(() => {
    if (!Token) {
      alert('올바른 경로로 접근하세요.');
      navigate('/');
    }
    const storedToken = localStorage.getItem('__bluecapsule__');

    if (storedToken) {
      try {
        const decodedToken: any = parseJwt(storedToken);

        if (decodedToken && decodedToken.name) {
          setUserName(decodedToken.name);
        }
      } catch (error) {
        console.error('Error decoding token:', error);
      }
    }

  }, [navigate]);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setFile(e.target.files[0]);
    }
  };

  const handleUpload = async () => {

    setUserName(decodedToken.name);

    try {
      if (!file) {
        alert("파일을 선택해주세요.")
        console.error('로그인이 안됐거나, 파일이 없거나..');
        return;
      }
      if (!userName) {
        alert("올바른 경로로 접근하셈.")
        console.error('로그인 안된 유저임.')
        return;
      }

      const formData = new FormData();
      formData.append('file', file);
      formData.append('id', userName);

      // console.log([...formData.entries()]);

      const response = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/upload`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${Token}`,
        },
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Upload successful:', data);
      } else {
        console.error('Upload failed:', response.statusText);
      }
    } catch (error: any) {
      console.error('Error during upload:', error.message);
    }
  };

  return (
    <>
      <Container>
        <span>{userName} 의 업로드 페이지</span>
        　
        <input type="file" accept="image/*" onChange={handleFileChange} />
        </Container>
        <Container>
          <Link to="/PersonalPage">
            <Button>
              <span>뒤로가기</span>
            </Button>
          </Link>
          <Button onClick={handleUpload}>
            <span>이미지 등록</span>
          </Button>
      </Container>
    </>
  );
};

export default Upload;

const Container = styled.div`
  display:grid;
  margin-top: 50px;
  justify-content: center;
  text-align:center;
`;

const Button = styled.button`
  margin-top: 20px;
  background: rgb(0, 172, 238);
  background: linear-gradient(0deg, rgba(0, 172, 0, 1) 0%, rgba(2, 126, 0, 1) 100%);
  width: 200px;
  height: 40px;
  line-height: 42px;
  padding: 0;
  border: none;
  border-radius: 5px;
  position: relative;
  color: white;
  outline: none;
  box-shadow: inset 2px 2px 2px 0px rgba(255, 255, 255, 0.5),
    7px 7px 20px 0px rgba(0, 0, 0, 0.1),
    4px 4px 5px 0px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;

  &:before,
  &:after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    background: rgba(2, 126, 0, 1);
    transition: all 0.3s ease;
  }

  &:before {
    height: 0%;
    width: 2px;
  }

  &:after {
    width: 0%;
    height: 2px;
  }

  &:hover {
    background: transparent;
    box-shadow: none;

    &:before {
      height: 100%;
    }

    &:after {
      width: 100%;
    }

    span {
      color: rgba(2, 126, 0, 1);

      &:before {
        height: 100%;
      }

      &:after {
        width: 100%;
      }
    }
  }

  span {
    position: relative;
    display: block;
    width: 100%;
    height: 100%;

    &:before,
    &:after {
      content: "";
      position: absolute;
      left: 0;
      bottom: 0;
      background: rgba(2, 126, 0, 1);
      transition: all 0.3s ease;
    }

    &:before {
      width: 2px;
      height: 0%;
    }

    &:after {
      width: 0%;
      height: 2px;
    }
  }
`;
