import React, { useState, useEffect } from 'react';
import styled from 'styled-components';


const Login: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [userName, setUserName] = useState<string | null>(null);
  const [token, setToken] = useState<string | null>(null);

  useEffect(() => {
    const storedToken = localStorage.getItem('__bluecapsule__');
  
    if (storedToken) {
      setToken(storedToken);
  
      try {
        const decodedToken = parseJwt(storedToken);
        setUserName(decodedToken ? decodedToken.name : null);
      } catch (error) {
        console.error('Error decoding token:', error);
        setUserName(null);
      }
  
      setLoggedIn(true);
    }
  }, []);

  const handleEmailChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPassword(e.target.value);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserName(null);
    setToken(null);
    localStorage.removeItem('__bluecapsule__');
  };

  const handleLogin = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
  
    try {
      const response = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });
  
      if (response.ok) {
        const data = await response.json();
  
        if (data.token) {
          const receivedToken = data.token;
          console.log('Received Token:', receivedToken);
  
          try {
            const decodedToken: any = parseJwt(receivedToken);
  
            if (decodedToken && decodedToken.name) {
              setUserName(decodedToken.name); // 토큰 중 이름값
              setLoggedIn(true);
              setToken(receivedToken);
              localStorage.setItem('__bluecapsule__', receivedToken);
            } else {
              console.error('Decoded token is invalid:', decodedToken);
              alert('서버로부터 유효하지 않은 토큰을 받음.');
            }
          } catch (decodeError) {
            console.error('Error decoding token:', decodeError);
            alert('토큰 로딩 오류발생. 다시 시도 요망');
          }
        } else {
          console.error('Token not found in server response : ', data);
          alert('서버로부터 토큰이 넘어오질 않았음.');
        }
      } else {
        const errorMessage = await response.text();
        console.error('Login failed:', errorMessage);
        alert('아이디 혹은 비밀번호가 틀렸음.');
      }
    } catch (error: any) {
      console.error('Error during login:', error.message);
      alert('로그인중에 알 수 없는 에러 발생함. 다시 시도 요망.');
    }
  };
  return (
    <LoginContainer>
      <Title>{isLoggedIn ? `${userName}` + '님 ㅎㅇ'  : 'Login'}</Title>
      {isLoggedIn ? (
        <LogoutForm>
          <Button onClick={handleLogout}>Logout</Button>
        </LogoutForm>
      ) : (
        <LoginForm onSubmit={handleLogin}>
          <Label>
            Email:
            <Input type="email" value={email} onChange={handleEmailChange} />
          </Label>
          <Label>
            Password:
            <Input type="password" value={password} onChange={handlePasswordChange} />
          </Label>
          <Button type="submit">Login</Button>
        </LoginForm>
      )}
    </LoginContainer>
  );
};

export default Login;

function parseJwt(token: string): any {
  if (!token) {
    console.error('Token is undefined or null');
    return null;
  }

  const parts = token.split('.');

  if (parts.length !== 3) {
    console.error('Token does not have the expected structure');
    return null;
  }

  const base64Url = parts[1];

  if (!base64Url) {
    console.error('Base64Url is undefined or null');
    return null;
  }

  const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');

  try {
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split('')
        .map((c) => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
        .join('')
    );

    return JSON.parse(jsonPayload);
  } catch (error) {
    console.error('Error parsing JWT:', error);
    return null;
  }
}

const LoginContainer = styled.div` 
max-width: 400px;
margin: auto;
margin-top:50px;

  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h2`
  text-align: center;
  color: #333;
`;

const LoginForm = styled.form`
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  margin-top: 20px;
`;

const LogoutForm = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 20px;
`;
const Label = styled.label`

  margin-bottom: 10px;
  font-size: 16px;
  color: #555;
`;

const Input = styled.input`
  padding: 8px;
  font-size: 14px;
`;

const Button = styled.button`
  background-color: #4caf50;
  color: white;
  padding: 10px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
`;
