import AnimatedBackground from "./style/AnimatedBackground";
import styled from 'styled-components';
import ShowImages from "./pages/ShowImages";
import bluecapsulelogo from './images/bluecapsule.jpg';
import { DelayedNavi, useLogout, ChecksToken } from './modules/Hooks';

function App() {
  const { isLoggedIn } = ChecksToken();
  const triggerNavi = DelayedNavi();
  const { handleLogout } = useLogout();

  const handleButtonClick = () => {
    triggerNavi('/PersonalPage');
  };

  const handlelogin = () => {
    triggerNavi('/login')
  }

  return (
    <>
      <Container>
          <Bcslogo src={bluecapsulelogo} />
          <>
          <Btn_intro>음음음</Btn_intro>
          <Btn_intro>상품음</Btn_intro>
          <Btn_intro>아무거나</Btn_intro>
          <Btn_intro>아아악</Btn_intro>
          </>
        {isLoggedIn ? (
          <>
            <Block>
              <Button onClick={handleLogout}>
                로그아웃
              </Button>
              <Button_pri onClick={handleButtonClick}>
                개인페이지
              </Button_pri>
            </Block>
          </>
        ) : (
          <>
            <Block>
              <Button onClick={handlelogin}>
                로그인
              </Button>
            </Block>
          </>
        )}
      </Container>
      <AnimatedBackground />
      <ShowImages />
    </>
  );
}

export default App;

const Container = styled.div`
  display:flex;
  position:relative;
  margin:auto;
  justify-content:center;
  align-items:center;
  width: 100%;
  height:90px;
  min-width:1200px;
`;
const Block = styled.div`
  position: absolute;
  display:flex;
  width:220px;
  right:20px;
  justify-content: space-evenly;
`;
const Button = styled.button`
  position:absoulte;
  width:100px;
  height:40px;
  border:none;
  border-radius:10px;
  background-color: rgba(0, 0, 0, 0.6);
  color: white;
  font-weight:bold;

  &:hover {
    background-color: rgba(0, 0, 0, 0.85);
  }
  &:active {
    background-color: rgba(0, 0, 0, 1);
  }
`;
const Button_pri = styled.button`
  position: relative;
  width:100px;
  height:40px;
  border:none;
  border-radius:10px;
  background-color: rgba(0, 0, 255, 0.6);
  color: white;
  font-weight:bold;

  &:hover {
    background-color: rgba(0, 0, 255, 0.85);
  }
  &:active {
    background-color: rgba(0, 0, 255, 1);
  }
`;
const Btn_intro = styled.button`
  width:15vw;
  height:50px;
  border: none;
  font-size:15px;
  font-weight:bold;
  background-color: transparent;
  min-width:180px;
  border-radius: 5px;

  &::before {
    content: '';
    bottom: 0;
    left: 0;
    width: 0;
    height: 5px; /* Adjust the thickness of the underline */
    background-color: rgba(0, 0, 255, 1);
    transition: width 0.3s ease; /* Adjust the duration and easing as needed */
  }

  &:hover::before {
    width: 100%;
  }
  }
`;
const Bcslogo = styled.img`
  position:absolute;
  left: 50px;
  width:100px;
  height:40px;
  border-radius: 5px;
  margin-left:10px;
`;
