import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Custom hook for delayed navigation
function useDelayedNavigation(): () => void {
  const navigate = useNavigate();
  const [delayedNavigation, setDelayedNavigation] = useState(false);

  const triggerDelayedNavigation = () => {
    setDelayedNavigation(true);

    setTimeout(() => {
      navigate('/PersonalPage');
    }, 500); // Move after 0.5 seconds
  };

  return triggerDelayedNavigation;
}

export { useDelayedNavigation };
