import { useLocation } from 'react-router-dom';
import { ShowPost } from '../modules/Hooks';
import styled from 'styled-components';
import { formatTime } from '../modules/Modules';
import Tab from './Tab';

const Viewpost: React.FC = () => {
    const location = useLocation();
    const fileName = location.state && location.state.fileName;
    const { title, images, contents, userName, day } = ShowPost(fileName);

    return (
      <>
        <Tab/>
        <Customed_table>
          <tbody>
            <tr>
              <td>
              {images.map((image, index) => (
                <Image key={index} src={image} alt={`Image ${index}`} />
              ))}
            </td>
            </tr>
            <tr>
              <td>{userName}</td>
            </tr>
            <Customed_hr/>
            <br/>
            <tr>
              <td>{title} </td>
            </tr>
            <br/>
            <tr>
              <td>{contents}</td>
            </tr>
            <br/>
            <tr>
              <td>게시물 등록 일자 : </td>
              <td>{day &&formatTime(day)}</td>
            </tr>
          </tbody>
        </Customed_table>
      </>
    );
  }
  
  export default Viewpost;

const Customed_table = styled.table`
  width:100%;
  hegiht:100%;
  display:flex;
  margin:auto;
  justify-content: center;
  align-items: center;
`;
const Image = styled.img`
  width:700px;
  height:500px;
  border-radius: 10px;
  cursor: pointer;
`;
const Customed_hr = styled.hr`
  border: 1px dotted rgba(125, 125, 125, 0.3);
`;
