import React, { useState } from 'react';
import styled from 'styled-components';

const Registration: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isEmailDuplicate, setIsEmailDuplicate] = useState(false);
  const [isNameDuplicate, setIsNameDuplicate] = useState(false);

  const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setName(e.target.value);
    setIsNameDuplicate(false);
  };
  const handleCheckNameDuplicate = async () => {
    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_SERVER_APP}/checkName`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ name }),
        }
      );

      if (response.ok) {
        const data = await response.json();
        if (data.isDuplicate) {
          setIsNameDuplicate(true);
          alert('이미 이름이 존재합니다.');
        } else {
          alert('사용 가능한 이름입니다.');
        }
      } else {
        console.error('Failed to check for duplicate name.');
      }
    } catch (error: any) {
      console.error('Error during duplicate check for name:', error.message);
    }
  };
  
  const handleEmailChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEmail(e.target.value);
    setIsEmailDuplicate(false);
  };

  const handlePasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPassword(e.target.value);
  };

  const handleResetForm = () => {
    setName('');
    setEmail('');
    setPassword('');
    setIsNameDuplicate(false);
    setIsEmailDuplicate(false);
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    try {
      const duplicateEmailResponse = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/checkEmail`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      if (duplicateEmailResponse.ok) {
        const duplicateEmailData = await duplicateEmailResponse.json();
        if (duplicateEmailData.isDuplicate) {
          setIsEmailDuplicate(true);
          alert('이미 이메일이 존재합니다.');
          return;
        }
      } else {
        console.error('Failed to check for duplicate email.');
        return;
      }

      const duplicateNameResponse = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/checkName`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name }),
      });

      if (duplicateNameResponse.ok) {
        const duplicateNameData = await duplicateNameResponse.json();
        if (duplicateNameData.isDuplicate) {
          setIsNameDuplicate(true);
          alert('이미 이름이 존재합니다.');
          return;
        }
      } else {
        console.error('Failed to check for duplicate name.');
        return;
      }

      const registrationResponse = await fetch(`${process.env.REACT_APP_API_SERVER_APP}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, password }),
      });

      if (registrationResponse.ok) {
        const data = await registrationResponse.json();
        alert('회원가입이 완료되었습니다.');
        console.log('Registration successful:', data);
        handleResetForm(); // Reset the form after successful registration
      } else {
        const errorData = await registrationResponse.json();
        console.error('Registration failed:', errorData.error);
      }
    } catch (error: any) {
      alert('다시 시도해주세요.');
      console.error('Error during registration:', error.message);
    }
  };

  return (
    <RegistrationContainer>
      <Title>Registration</Title>
      <Form onSubmit={handleSubmit}>
      <Label>
        Name:
        <Input
          type="text"
          value={name}
          onChange={handleNameChange}
          style={{ borderColor: isNameDuplicate ? 'red' : '' }}
        />
        <br />
        <CheckDuplicatesButton onClick={handleCheckNameDuplicate}>
          Check for name duplicates
        </CheckDuplicatesButton>
        {isNameDuplicate && <ErrorMessage>The name already exists</ErrorMessage>}
      </Label>
        <Label>
          Email:
          <Input
            type="email"
            value={email}
            onChange={handleEmailChange}
            style={{ borderColor: isEmailDuplicate ? 'red' : '' }}
          />
          <br />
          {isEmailDuplicate && <ErrorMessage>이미 이메일이 존재합니다.</ErrorMessage>}
        </Label>
        <Label>
          Password:
          <Input type="password" value={password} onChange={handlePasswordChange} />
        </Label>
        <Button type="submit">Register</Button>
        <ResetButton type="button" onClick={handleResetForm}>
          Reset Form
        </ResetButton>
      </Form>
    </RegistrationContainer>
  );
};

export default Registration;

const RegistrationContainer = styled.div`
  max-width: 400px;
  margin: auto;
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h2`
  text-align: center;
  color: #333;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  margin-top: 20px;
`;

const Label = styled.label`
  margin-bottom: 10px;
  font-size: 16px;
  color: #555;
`;

const Input = styled.input`
  padding: 8px;
  font-size: 14px;
`;

const Button = styled.button`
  background-color: #4caf50;
  color: white;
  padding: 10px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
  margin-top: 10px;
`;

const ResetButton = styled(Button)`
  background-color: #ccc;
  margin-top: 10px;
`;

const ErrorMessage = styled.span`
  color: red;
`;

const CheckDuplicatesButton = styled.button`
  background-color: #3498db;
  color: white;
  padding: 5px 10px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  margin-top: 5px;
`;

export {
  RegistrationContainer,
  Title,
  Form,
  Label,
  Input,
  Button,
  ResetButton,
  ErrorMessage,
  CheckDuplicatesButton
};
