import { useLocation } from 'react-router-dom';
import { ShowPost } from '../modules/Hooks';
import styled from 'styled-components';
import { formatTime } from '../modules/Modules';
import { useEffect } from 'react';
import Tab from './Tab';

const Viewpost: React.FC = () => {

    const location = useLocation();
    const fileName = location.state && location.state.fileName;
    const { title, images, contents, userName, day } = ShowPost(fileName);

    useEffect(() => {
        const params = new URLSearchParams(location.search);
        const postId = params.get('postId');
    
        console.log('postId:', postId);
    
      }, [location.search]);
    return (
      <>
        <Tab/>
        <Customed_table>
            <tr>
              <td>
              {images.map((image, index) => (
                <Image key={index} src={image} alt={`Image ${index}`} />
              ))}
            </td>
            </tr>
            <Customed_tr>
              <td><h3>{userName}</h3></td>
            </Customed_tr>
            <tr>
            <Customed_hr/>
            </tr>
            <Customed_tr>
              <td><h3>{title}</h3></td>
            </Customed_tr>

            <Customed_tr>
              <td>{contents}</td>
            </Customed_tr>

            <Customed_tr>
              <td>등록 일자 : {day &&formatTime(day)}</td>
            </Customed_tr>
        </Customed_table>
        
      </>
    );
  }
  
  export default Viewpost;

const Customed_table = styled.table`
  hegiht:100%;
  display:grid;
  margin:auto;
  margin-top:20px;
  justify-content: center;
  align-items: center;
`;
const Image = styled.img`
  width:700px;
  height:500px;
  border-radius: 10px;
  cursor: pointer;
`;
const Customed_hr = styled.hr`
  border: 1px solid rgba(125, 125, 125, 0.2);
`;
const Customed_tr = styled.tr`
  padding:7.5px;
`;
