import { useState, useEffect } from 'react';
import styled from 'styled-components';

const Chat = () => {
  const [messages, setMessages] = useState<(string | ArrayBuffer)[]>([]);
  const [localMessages, setLocalMessages] = useState<string[]>([]);
  const [currentMessage, setCurrentMessage] = useState<string>('');
  const [socket, setSocket] = useState<WebSocket | null>(null);

  useEffect(() => {
    const newSocket = new WebSocket('ws://192.168.0.23:4002');

    newSocket.onopen = () => {
      console.log('WebSocket connection opened');
    };

    newSocket.onmessage = (event) => {
      try {
        const data = event.data;
    
        if (typeof data === 'string') {
          try {
            // Attempt to parse the message as JSON
            const parsedMessage = JSON.parse(data);
    
            // Check the type of the message
            if (parsedMessage.type === 'text') {
              setMessages((prevMessages) => [...prevMessages, parsedMessage.content]);
            } else {
              // Handle other types of messages (e.g., binary data)
              // Add your logic here if needed
            }
          } catch (jsonError) {
            // If parsing as JSON fails, treat the message as a regular string
            setMessages((prevMessages) => [...prevMessages, data]);
          }
        } else if (data instanceof Blob) {
          // Handle Blob data as binary
          const reader = new FileReader();
          reader.onload = () => {
            setMessages((prevMessages) => [
              ...prevMessages,
              reader.result! as string | ArrayBuffer,
            ]);
          };
          reader.readAsArrayBuffer(data);
        } else if (data instanceof ArrayBuffer) {
          // Convert ArrayBuffer to text using TextDecoder
          const text = new TextDecoder().decode(data);
          setMessages((prevMessages) => [...prevMessages, text]);
        }
      } catch (error) {
        console.error('Error in onmessage:', error);
      }
    };

    newSocket.onclose = () => {
      console.log('WebSocket connection closed');
    };

    setSocket(newSocket);

    return () => {
      try {
        newSocket.close();
      } catch (error) {
        console.error('Error in cleanup:', error);
      }
    };
  }, []);
}

  const sendMessage = () => {
    try {
      if (socket && socket.readyState === WebSocket.OPEN) {
        // Send message as JSON
        socket.send(JSON.stringify({ type: 'text', content: currentMessage }));
        setLocalMessages((prevLocalMessages) => [...prevLocalMessages, currentMessage]);
        setCurrentMessage('');
      }
    } catch (error) {
      console.error('Error in sendMessage:', error);
    }

  return (
    <div>
      <h2>Chat Room</h2>
      <div>
        <ul>
          {messages.map((message, index) => (
            <Li key={index}>
              {typeof message === 'string' ? (
                message
              ) : (
                <span>{`${message}`}</span>
              )}
            </Li>
          ))}
        </ul>
        <ul>
          {localMessages.map((localMessage, index) => (
            <Li key={index}>{localMessage}</Li>
          ))}
        </ul>
      </div>
      <div>
        <input
          type="text"
          value={currentMessage}
          onChange={(e) => setCurrentMessage(e.target.value)}
          placeholder="Type your message here"
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

const Li = styled.li`
  list-style: none;
`;

export default Chat;
