import React, { useState } from 'react';
import styled from 'styled-components';

const RegistrationContainer = styled.div`
  max-width: 400px;
  margin: auto;
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h2`
  text-align: center;
  color: #333;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  margin-top: 20px;
`;

const Label = styled.label`
  margin-bottom: 10px;
  font-size: 16px;
  color: #555;
`;

const Input = styled.input`
  padding: 8px;
  font-size: 14px;
`;

const Button = styled.button`
  background-color: #4caf50;
  color: white;
  padding: 10px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
`;

const Registration: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isDuplicate, setIsDuplicate] = useState(false);

  const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setName(e.target.value);
  };

  const handleEmailChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEmail(e.target.value);
    setIsDuplicate(false);
  };

  const handlePasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    try {
      const duplicateCheckResponse = await fetch(
        `${process.env.REACT_APP_API_SERVER_APP}/checkDuplicateEmail`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email }),
        }
      );

      if (duplicateCheckResponse.ok) {
        const duplicateCheckData = await duplicateCheckResponse.json();
        if (duplicateCheckData.isDuplicate) {
          setIsDuplicate(true);
          console.error('Email is already registered.');
          return;
        }
      } else {
        console.error('Failed to check for duplicate email.');
        return;
      }

      const registrationResponse = await fetch(
        `${process.env.REACT_APP_API_SERVER_APP}/register`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ name, email, password }),
        }
      );

      if (registrationResponse.ok) {
        const data = await registrationResponse.json();
        console.log('Registration successful:', data);
      } else {
        const errorData = await registrationResponse.json();
        console.error('Registration failed:', errorData.error);
      }
    } catch (error: any) {
      console.error('Error during registration:', error.message);
    }
  };

  return (
    <RegistrationContainer>
      <Title>Membership Registration</Title>
      <Form onSubmit={handleSubmit}>
        <Label>
          Name:
          <Input type="text" value={name} onChange={handleNameChange} />
        </Label>
        <Label>
          Email:
          <Input
            type="email"
            value={email}
            onChange={handleEmailChange}
            style={{ borderColor: isDuplicate ? 'red' : '' }}
          />
          {isDuplicate && <span style={{ color: 'red' }}>Email is already registered.</span>}
        </Label>
        <Label>
          Password:
          <Input type="password" value={password} onChange={handlePasswordChange} />
        </Label>
        <Button type="submit">Register</Button>
      </Form>
    </RegistrationContainer>
  );
};

export default Registration;
