import AnimatedBackground from "./style/AnimatedBackground";
import styled from 'styled-components';
import ShowImages from "./pages/ShowImages";
import bluecapsulelogo from './images/bluecapsule.jpg';
import { DelayedNavi, useLogout, ChecksToken } from './modules/Hooks';

function App() {
  const { isLoggedIn } = ChecksToken();
  const triggerNavi = DelayedNavi();
  const { handleLogout } = useLogout();

  const handleButtonClick = () => {
    triggerNavi('/PersonalPage');
  };

  const handlelogin = () => {
    triggerNavi('/login')
  }

  return (
    <>
      <Container>
        <Block>
          <Bcslogo src={bluecapsulelogo} />
        </Block>
      {isLoggedIn ? (
        <>
          <Block>
          <Button onClick={handleLogout}>
            로그아웃
          </Button>
          </Block>
          <Block>
          <Button_pri onClick={handleButtonClick}>
            개인페이지
          </Button_pri>
          </Block>
        </>
      ) : (
        <>
        <Block>
          <Button onClick={handlelogin}>
            로그인
          </Button>
          </Block>
        </>
      )}
      </Container>
      <AnimatedBackground />
      <ShowImages />
    </>
  );
}

export default App;

const Container = styled.div`
  display:flex;
  width: 100%;
  height:100px;
  background-color: rgba(1, 25, 233, 0.05);
`;
const Block = styled.div `

`;
const Button = styled.button`
  width:100px;
  height:40px;
  margin-left:5px;
  border:none;
  border-radius:10px;
  background-color: rgba(0, 0, 0, 0.6);
  color: white;
  font-weight:bold;

  &:hover {
    background-color: rgba(0, 0, 0, 0.85);
  }
  &:active {
    background-color: rgba(0, 0, 0, 1);
  }
`;
const Button_pri = styled.button`
  width:100px;
  height:40px;
  margin-left:5px;
  border:none;
  border-radius:10px;
  background-color: rgba(0, 0, 255, 0.6);
  color: white;
  font-weight:bold;

  &:hover {
    background-color: rgba(0, 0, 255, 0.85);
  }
  &:active {
    background-color: rgba(0, 0, 255, 1);
  }
`;
const Bcslogo = styled.img`
  width:150px;
  height:50px;
  border-radius: 5px;
`;
