import { useParams } from 'react-router-dom';
import { ShowPost } from '../modules/Hooks';
import styled from 'styled-components';

const Viewpost: React.FC = () => {
    const { fileName } = useParams<{ fileName?: string }>();

    if (!fileName) {
        return <div>No post found.</div>;
      }
      
    const { title, images, contents, userName, day } = ShowPost(fileName);

    return (
      <>
        <Customed_table>
          <tbody>
            <tr>
              <td>사진 영역</td>
              <td>
                {images && images.map((image: any, index: any) => (
                  <img key={index} src={image} alt='fail' />
                ))}
              </td>
            </tr>
            <tr>
              <td>테스트용 제목</td>
              <td>{title} </td>
            </tr>
            <tr>
              <td>게시물 내용</td>
              <td>{contents}</td>
            </tr>
            <tr>
              <td>유저명</td>
              <td>{userName}</td>
            </tr>
            <tr>
              <td>날짜</td>
              <td>{day}</td>
            </tr>
          </tbody>
        </Customed_table>
      </>
    );
  }
  
  export default Viewpost;

const DIV = styled.div`
    position: relative;
    width: 400px;
    height:700px;
    background-color:white;
    box-shadow: 1px 6px 6px 1px rgba(0, 0, 0, 0.2);
    border-radius: 7.5px;
    margin-left:50px;
    margin-top: 40px;
`; 

const Customed_table = styled.table`

`
